import React, {Component} from 'react'
import{Text, View, StyleSheet} from 'react-native'
import { db } from '../firebase/config'

class Home extends Component{
    constructor(props){
        super(props);
        this.setState ={
            posts:[]
        }
    }

    componentDidMount(){
        this.showPost();
    }

    showPost(){
        db.collection('posteos').onSnapshot((docs)=>{
            console.log(docs)
            let posteos = []
            docs.forEach((doc)=> {
                console.log(doc.data())
                posteos.push({
                    id: doc.id,
                    data: doc.data()
                })
            })
            this.setState({
                posts: posteos
            })
        })
    }

    render(){
        return(
            <View style={StyleSheet.container}>
                <Text>Home</Text>
            </View>

        )
    }
}

export default Home;
