import React, { Component } from 'react'
import { Text, StyleSheet, View, Modal, TouchableOpacity, Image } from 'react-native'
import { auth, db } from '../firebase/config'
import firebase from 'firebase'
import Comments from '../components/Comments';

class Post extends Component {
    constructor(props) {
        super(props)
        this.state = {
            likes: 0,
            liked: false,
            showModal: false
        }
    }

    componentDidMount() {
        this.recieveLikes();
    }

    recieveLikes() {
        let likes = this.props.postData.data.likes;
        if (likes) {
            this.setState({
                likes: likes.length
            })
        }
        if(likes.includes(auth.currentUser.email)) {    
            this.setState({
                liked: true
            })
        }
    }

    likePost() {
        let post = db.collection("posteos").doc(this.props.postData.id);

        post.update({
            likes: firebase.firestore.FieldValue.arrayUnion(auth.currentUser.email)
        })
        .then(() => {
            this.setState({
                likes: this.state.likes + 1,
                liked: true
            })
            console.log('likeado');
        })
        .catch((error) => {
            console.error("Error updating document: ", error); 
        });
    }

    unlikePost() {
        let post = db.collection("posteos").doc(this.props.postData.id);

        post.update({
            likes: firebase.firestore.FieldValue.arrayRemove(auth.currentUser.email)
        })
        .then(() => {
            this.setState({
                likes: this.state.likes - 1,
                liked: false
            })
            console.log('deslikeado');
        })
        .catch((error) => {
            console.error("Error updating document: ", error);
        });
    }

    openModal() {
        this.setState({
            showModal: true
        })
    }

    closeModal() {
        this.setState({
            showModal: false
        })
    }


    render() {
        return (
            <View style={styles.container}>
                 <Image 
                    style={styles.preview}
                    source={this.props.postData.data.photo}
                /> 
                <Text> {this.props.postData.data.user} </Text>
                <Text> {this.props.postData.data.title} </Text>
                <Text> {this.props.postData.data.description} </Text>
                <Text>Likes: {this.state.likes}</Text>
                <TouchableOpacity onPress={() => this.openModal()}>
                    <Text> Ver comentarios</Text>
                </TouchableOpacity>
                {
                    ! this.state.liked ?
                        <TouchableOpacity style={styles.button} onPress={() => this.likePost()}>
                            <Text style={styles.textButton}>Like</Text>
                        </TouchableOpacity>
                    :
                        <TouchableOpacity style={styles.button2} onPress={() => this.unlikePost()}>
                            <Text style={styles.textButton}>Unlike</Text>
                        </TouchableOpacity>
                }
                {this.props.postData.data.user == auth.currentUser.email ? (
                <TouchableOpacity
                    onPress={()=>
                        this.props.deletePost(this.props.createdAt)
                    }
                    style={styles.delete}
                > X
                </TouchableOpacity>
                )
                : null
                }

                {
                     this.state.showModal ? 
                        <Modal 
                            style={styles.modalContainer}
                            visible={this.state.showModal}
                            animationType="slide"
                            transparent={false}
                        >
                            <View style={styles.modalView}>
                            <TouchableOpacity onPress={() => this.closeModal()} style={styles.closeModal}>
                                <Text>X</Text>
                            </TouchableOpacity>
                            <Comments
                                comments={this.props.postData.data.comments}
                                postId={this.props.postData.id}
                            />
                            </View>
                        </Modal>
                    :
                        null

                }
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
        marginVertical: 15,
        shadowColor: "#ccc",
        shadowOffset: {
            width: 0,
            height: 0,
        },
        shadowOpacity: 0.5,
        shadowRadius: 10,
        borderRadius: 5,
    },
    button: {
        backgroundColor: "#28a745",
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: "center",
        borderRadius: 4,
        borderWidth: 1,
        borderStyle: "solid",
        borderColor: "#28a745",
    },
    button2: {
        backgroundColor: '#dc3545',
        paddingHorizontal: 10,
        paddingVertical: 6,
        textAlign: "center",
        borderRadius: 4,
        borderWidth: 1,
        borderStyle: "solid",
        borderColor: '#dc3545',
    },
    textButton: {
        color: "#fff",
    },
    modalContainer: {
        // width:'100%',  
        // flex: 3,
        // alignSelf: 'center',
        // backgroundColor: "white",
        // borderColor: '#000000',
        // borderRadius: 6,
        // padding: 10,
        // backgroundColor: '#000000'
        border: 'none',
        width: '100%',

    },
    closeModal:{
        alignSelf: 'flex-end',
        padding: 10,
        backgroundColor: '#dc3545',
        marginTop:2,
        borderRadius: 4,
    },
    modalText:{
        fontWeight: 'bold',
        color: 'black'
    },
    modalView:{
        backgroundColor: 'lightblue',
        borderRadius: 4,
        padding: 4,
    },
    preview: {
        width: '100%',
        height: 500,
        borderRadius: 10,
    },
    delete:{
        fontFamily: 'Arial',
        color: 'white',
        textAlign: 'center',
        backgroundColor: '#dc3545',
        borderRadius: 8,
        margin: 8,
        padding: 4,
        width: '10%',
    },
});

export default Post
